commits.content:
@import "base/mixin";

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html{
    font-size: 62.5%;
}

@font-face {
    font-family: "sansBold";
    src: url(/fontFamily/DM_Sans/DMSans-Bold.ttf) format("ttf");
    font-display: swap;
}

@font-face {
    font-family: "sansItalic";
    src: url(/fontFamily/DM_Sans/DMSans-Medium.ttf) format("opentype");
    font-display: swap;
}

@font-face {
    font-family: "sansBoldItalic";
    src: url(/fontFamily/DM_Sans/DMSans-BoldItalic.ttf) format("ttf");
    font-display: swap;
}

/*BODY*/

body{
    height: 100vh;
    width: 100%;
    background: #000;
}

body::before,
body::after{
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 100vh;
    width: 100%;
    opacity: 0.4;
}

body::before{
    clip-path: circle(30% at left 20%);
    background: linear-gradient(135deg, #B69121 100%, #B69121 100%);
}

body::after{
    clip-path: circle(18% at right 70%);
    background: linear-gradient(135deg, #B69121 10%, #B69121 100%);
}

/*HEADER*/
header{
    height: 90vh;
    width: 98%;
    background: rgba(255,255,255,0.1);
    backdrop-filter: blur(5px);
    margin: 25px 0 0 12px;
    border-radius: 25px;
    box-shadow: 0 20 px 50px rgba(0,0,0,0.15);
    border-top: 1px solid rgba(255,255,255,0.5);
    border-left: 1px solid rgba(255,255,255,0.5);
    display: flex;
    align-items: center;

    .navbar{
        width: 100%;
        display: flex;
        padding: 35px 50px;
        justify-content: space-between;

        .menu{
            display: flex;

                li{
                    list-style: none;
                    margin: 0 15px;

                    .link-wrapper{
                        position: relative;
                        left: 700px;
                        top: 250px;
                        display: block;
                        padding: 8px 0;
                    }

                    a{
                        font-size: 17px;
                        text-transform: uppercase;
                        color: #ffffff;
                        text-decoration: none;
                        font-weight: 500;
                        transition: all 0.3s ease;

                            &:hover{
                                color: #ffffff; 
                                font-weight: 200;
                            }
                    }

                    .link{
                        display: inline-block;
                        position: relative;
                        text-decoration: none;
                        padding: 10px 0;
                        color: #fff;
                    }   

                    .hover-2 { 

                        &:after {
                            content: '';
                            position: absolute;
                            bottom: 0;
                            left: 0;
                            right: 0;
                            width: 100%;
                            height: 3px;
                            transform: scaleX(0);
                            background-color: #B69121;
                            transition: transform 0.3s;
                        }

                        &:hover {
                            &:after {
                                transform: scaleX(1);    
                            }
                        }

                    }

                }

        }

        .btn {
            width: 316px;
            padding: 5px 100px 5px 6px;
            border: 1px;
            margin: 0px 100px 0px 8px;

            button{
                color: #f2f2f2;
                background-color: transparent;
                font-family: inherit;
                font-weight: 600;
                line-height: 1.5;
                outline: none;
                font-size: 17px;
                padding: 3px 12px;
                border: 1px solid #f2f2f2;
                cursor: pointer;
                transition: all 0.3s ease;

                    &:hover{
                        color: wheat;
                        border: 1px solid wheat;
